import React from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { techniques } from '../data/techniques';

const TechniqueDetail: React.FC = () => {
  const { id } = useParams<{ id: string }>();
  const navigate = useNavigate();

  const technique = techniques.find(t => t.id === id);

  if (!technique) {
    return (
      <div className="container">
        <div className="card">
          <h2>Technique Not Found</h2>
          <p>The technique you're looking for doesn't exist.</p>
          <button className="btn back-btn" onClick={() => navigate('/')}>
            Back to Techniques
          </button>
        </div>
      </div>
    );
  }

  return (
    <div className="container">
      <button className="btn back-btn" onClick={() => navigate('/')}>
        ‚Üê Back to Techniques
      </button>

      <div className="header">
        <h1>{technique.name}</h1>
        <p><strong>Category:</strong> {technique.category}</p>
      </div>

      <div className="section">
        <div className="card">
          <h2>Origin</h2>
          <p>{technique.origin}</p>
        </div>
      </div>

      <div className="section">
        <div className="card">
          <h2>Description</h2>
          <p>{technique.description}</p>
        </div>
      </div>

      <div className="section">
        <div className="card">
          <h2>Best Response</h2>
          <p>{technique.bestResponse}</p>
        </div>
      </div>
    </div>
  );
};

export default TechniqueDetail;
